export default {

	setUpperCaseState: (state) => {

	if(state == 'sent'){
		return 'Sent'
	}
		else if(state == 'soft-bounced'){
			return 'Soft-Bounced'
		}
		else if(state == 'rejected'){
			return 'Rejected'
		}
	},

	getDate: (date) => {
		date = new Date().setDate(new Date().getDate()-7);
		return new Date(date).toISOString();
}
}
