export default {

	container1_button1_onclick: () => {

				//query to run
		container2_select_record.run();

	//select query and table to pull data from
		if(appsmith.store.table === 'jobs'){
			storeValue('query', container2_select_record.data[0]);
			storeValue('container2_record_type','properties');
		}
		if(appsmith.store.table === 'invoices'){
		storeValue('query',container2_select_record.data[0]);
		storeValue('container2_record_type', 'customers');
		}
		else if(appsmith.store.table === 'emails'){
		storeValue('query',container2_select_record.data[0]);
		storeValue('container2_record_type', 'jobs');
		}


	},
	container1_button2_onclick: () => {


		//query to run
		container2_select_record2.run();

		//select query and table to pull data from
		if(appsmith.store.table === 'invoices'){
		storeValue('query', container2_select_record2.data[0]);
		storeValue('container2_record_type', 'jobs');
		}


	},

		container1_button3_onclick: () => {

		//select query and table to pull data from
		if(appsmith.store.table === 'invoices'){
		storeValue('query', container2_select_record3.data[0]);
		storeValue('container2_record_type', 'emails');
		}


		//query to run
		container2_select_record3.run();



	},

	container2_button1_onclick: () => {

		container3_select_record.run();

	}
}
